import React from 'react';
import './form-input.scss';

const FormInput = (handleChange, label, ...otherProps) => (
  <div className='group'>
    <input
      onChange={handleChange}
      {...otherProps}
      className='form-input'
      type='text'
    />
    {label ? (
      <label
        className={`${
          otherProps.value.length ? 'shrink' : ''
        } form-input-label`}
        htmlFor=''
      >
        {label}
      </label>
    ) : null}
  </div>
);

export default FormInput;
